css:
.item-count-box {
  float: right;
  min-width: 26px;
  text-align: center;
  display: inline;
  font-size: 12px;
  padding: 1px 5px 1px 5px;
  margin-top: 3.5px;
  line-height: 20.9px;
  border-radius: 3px;
}
#account-switcher,
#account-sidebar {
  order: 1;
  height: 100%;
  background-color: #f6f7f8;
}
#account-switcher section,
#account-sidebar section {
  padding-bottom: 6px;
}
#account-switcher .heading,
#account-sidebar .heading {
  color: rgba(35, 31, 32, 0.5);
  font-weight: 600;
  font-size: 12px;
  padding-left: 10px;
  padding-top: 10px;
  letter-spacing: -0.2px;
}
#account-switcher .item,
#account-sidebar .item {
  color: rgba(35, 31, 32, 0.8);
  font-size: 13.95px;
  font-weight: 400;
  padding: 0 15px;
  line-height: 31.46px;
  clear: both;
}
#account-switcher .item img.content-mask,
#account-sidebar .item img.content-mask {
  background-color: rgba(35, 31, 32, 0.8);
  vertical-align: text-bottom;
}
#account-switcher .item .unread,
#account-sidebar .item .unread {
  font-weight: 500;
  color: #f6f7f8;
  background: #3187e1;
  margin-left: 10px;
}
#account-switcher .item .icon,
#account-sidebar .item .icon {
  float: left;
}
#account-switcher .item .name,
#account-sidebar .item .name {
  padding-left: 10px;
  position: relative;
  top: 1px;
  overflow: hidden;
  padding-top: 5px;
  padding-bottom: 5px;
  line-height: 20.9px;
}
#account-switcher .item.selected,
#account-sidebar .item.selected {
  background: #f6f7f8;
  color: #3187e1;
}
#account-switcher .item.selected img.content-mask,
#account-sidebar .item.selected img.content-mask {
  background-color: #3187e1;
}
#account-switcher .item.dropping,
#account-sidebar .item.dropping {
  background-color: #ffffff;
  color: #3187e1;
}
#account-switcher .item.dropping img.content-mask,
#account-sidebar .item.dropping img.content-mask {
  background-color: #3187e1;
}
#account-switcher .item:hover,
#account-sidebar .item:hover {
  cursor: default;
}
#account-switcher .item-divider,
#account-sidebar .item-divider {
  color: #586870;
  padding-top: 1em;
  padding-bottom: 0.25em;
}
#account-sidebar .name {
  text-transform: capitalize;
}
#account-switcher {
  border-bottom: 1px solid #dddddd;
}
#account-switcher .primary-item {
  padding-top: 10px;
  padding-bottom: 6px;
  padding-left: 10px;
}
#account-switcher .primary-item .name {
  padding-left: 7px;
}
#account-switcher .name {
  text-transform: lowercase;
  overflow: hidden;
  text-overflow: ellipsis;
  white-space: nowrap;
}
#account-switcher .toggle {
  transform: rotateX(0deg);
}
#account-switcher .dropdown {
  opacity: 0;
  pointer-events: none;
  transform: scale(1, 0.2);
  transform-origin: top;
  transition: transform 125ms cubic-bezier(0.18, 0.89, 0.32, 1.12), opacity 100ms linear;
  margin-top: -7px;
  background: #ffffff;
  border: 1px solid #dddddd;
  border-radius: 4px;
  border-top-left-radius: 0;
  border-top-right-radius: 0;
  box-shadow: 0 2px 4px 0 rgba(0, 0, 0, 0.21);
  position: absolute;
  top: 54px;
  width: 100%;
  z-index: 999;
}
#account-switcher .dropdown .inner {
  opacity: 0;
  transition: opacity 25ms linear;
  transition-delay: 0;
}
#account-switcher .dropdown .account .gravatar {
  top: 6px;
}
#account-switcher .item {
  position: relative;
  margin-bottom: 0;
  display: block;
}
#account-switcher .item .gravatar {
  background-size: 28px 28px;
  width: 28px;
  height: 28px;
  position: absolute;
  z-index: 2;
  border-radius: 4px;
  top: -2px;
  background-repeat: no-repeat;
}
#account-switcher .secondary-item {
  padding: 6px 5px 0 14px;
}
#account-switcher .secondary-item:hover {
  background: #f5f5f5;
}
#account-switcher .secondary-item:first-child {
  padding-top: 8px;
  border-top-left-radius: 4px;
  border-top-right-radius: 4px;
}
#account-switcher .secondary-item:last-child {
  padding-bottom: 2px;
  border-bottom-left-radius: 4px;
  border-bottom-right-radius: 4px;
}
#account-switcher.open .dropdown {
  opacity: 1;
  pointer-events: initial;
  transform: scale(1, 1);
  transform-origin: top;
}
#account-switcher.open .dropdown .inner {
  opacity: 1;
  transition: opacity 50ms linear;
  transition-delay: 50ms;
}
#account-switcher.open .toggle {
  transform: rotateX(180deg);
}
#account-switcher {
  height: auto;
}
