css:
.item-count-box {
  float: right;
  min-width: 26px;
  text-align: center;
  display: inline;
  font-size: 12px;
  padding: 1px 5px 1px 5px;
  margin-top: 3.5px;
  line-height: 20.9px;
  border-radius: 3px;
}
body.window-type-react-remote {
  margin: 0;
}
body.window-type-react-remote .sheet-toolbar {
  border-bottom: none;
  height: 30px;
  min-height: 30px;
  max-height: 30px;
  display: none;
}
body.window-type-react-remote .sheet-toolbar .toolbar-window-controls {
  margin-top: 3px;
  margin-left: 3px;
}
body.window-type-react-remote .sheet-toolbar .window-title {
  line-height: 30px;
}
body.window-type-react-remote .sheet {
  background: #f6f6f6;
}
body.platform-darwin.window-type-react-remote .sheet-toolbar {
  display: inherit;
}
body.is-blurred .preference-header {
  background-image: -webkit-linear-gradient(top, #f6f6f6, #f6f6f6);
}
body.platform-darwin .preferences-wrap .platform-darwin-only {
  display: inherit;
}
.preferences-wrap {
  padding-bottom: 50px;
  background-color: #ffffff;
  color: #231f20;
}
.preferences-wrap input[type=checkbox] {
  margin: 0 7px 0 0;
  position: relative;
  top: -1px;
}
.preferences-wrap input[type=radio] {
  margin: 0 7px 0 0;
  position: relative;
  top: -1px;
}
.preferences-wrap select {
  margin: 10px;
  margin-top: 4px;
}
.preferences-wrap .platform-darwin-only {
  display: none;
}
.preferences-wrap .well {
  border-radius: 7px;
  border: 1px solid #dddddd;
  background-color: #fbfbfb;
}
.preferences-wrap .well.large {
  padding: 20px;
  padding-right: 10px;
}
.preferences-wrap .well.small {
  padding: 10px;
}
.preferences-wrap .section {
  margin-top: 25px;
}
.preferences-wrap .section-header {
  line-height: 2em;
  margin-top: 7px;
  margin-bottom: 2px;
  cursor: default;
}
.preferences-wrap .section-body {
  padding: 5px;
  padding-top: 10px;
  padding-bottom: 10px;
}
.preferences-wrap .section-body .section-body-list {
  flex: 1;
}
.preferences-wrap .section-body .section-body-list img {
  width: 100%;
  padding: 15px;
}
.preferences-wrap .container-signatures {
  width: 95%;
  margin-left: 2.5%;
  margin-right: 2.5%;
}
.preferences-wrap .container-signatures .section-body {
  padding: 10px 0 0 0;
}
.preferences-wrap .container-signatures .section-body .menu {
  border: solid thin #CCC;
  margin-right: 5px;
  min-height: 200px;
}
.preferences-wrap .container-signatures .section-body .menu .menu-items {
  margin: 0;
  padding: 0;
  list-style: none;
}
.preferences-wrap .container-signatures .section-body .menu .menu-items li {
  padding: 6px;
}
.preferences-wrap .container-signatures .section-body .menu-horizontal {
  height: 100%;
}
.preferences-wrap .container-signatures .section-body .menu-horizontal .menu-items {
  height: 100%;
  margin: 0;
  padding: 0;
  list-style: none;
}
.preferences-wrap .container-signatures .section-body .menu-horizontal .menu-items li {
  text-align: center;
  width: 40px;
  display: inline-block;
  padding: 8px 16px 8px 16px;
  border-right: solid thin #CCC;
}
.preferences-wrap .container-signatures .section-body .signature-area {
  border: solid thin #CCC;
  min-height: 200px;
}
.preferences-wrap .container-signatures .section-body .menu-footer {
  border: solid thin #CCC;
  overflow: auto;
}
.preferences-wrap .container-signatures .section-body .signature-footer {
  border: solid thin #CCC;
  overflow: auto;
}
.preferences-wrap .container-signatures .section-body .signature-footer .edit-html-button {
  float: right;
  margin: 6px;
}
.preferences-wrap .container-notifications {
  width: 75%;
  margin-left: 12.5%;
  margin-right: 12.5%;
}
.preferences-wrap .container-appearance {
  width: 70%;
  margin: auto;
}
.preferences-wrap .container-appearance .appearance-mode {
  background-color: #fdfdfd;
  border-radius: 10px;
  border: 1px solid #6d7987;
  text-align: center;
  flex: 1;
  padding: 25px;
  padding-bottom: 9px;
  margin: 10px;
  margin-top: 0;
}
.preferences-wrap .container-appearance .appearance-mode img {
  background-color: #6d7987;
}
.preferences-wrap .container-appearance .appearance-mode div {
  margin-top: 15px;
  text-transform: capitalize;
  cursor: default;
}
.preferences-wrap .container-appearance .appearance-mode.active {
  border: 1px solid #3187e1;
  color: #3187e1;
}
.preferences-wrap .container-appearance .appearance-mode.active img {
  background-color: #3187e1;
}
.preferences-wrap .container-keymaps {
  width: 75%;
  margin-left: 12.5%;
  margin-right: 12.5%;
}
.preferences-wrap .container-keymaps .shortcut {
  padding: 3px 0;
}
.preferences-wrap .container-keymaps .shortcut.shortcut-select {
  padding: 5px 0 20px 0;
}
.preferences-wrap .container-keymaps .shortcut.shortcut-select select {
  width: 75%;
}
.preferences-wrap .container-keymaps .shortcut .shortcut-name {
  text-align: right;
  flex: 1;
  margin-right: 20px;
}
.preferences-wrap .container-keymaps .shortcut .shortcut-value {
  text-align: left;
  flex: 1;
}
.preferences-wrap .container-keymaps .shortcuts-extras {
  text-align: center;
  margin-top: 20px;
}
.preferences-wrap .container-accounts {
  margin-left: 20px;
  margin-right: 20px;
}
.preferences-wrap .container-accounts .account-name {
  font-size: 18px;
  cursor: default;
}
.preferences-wrap .container-accounts .account-subtext {
  font-size: 13.95px;
  cursor: default;
}
.preferences-wrap .preference-header {
  margin-bottom: 15px;
  border-bottom: 1px solid #bbbbbb;
  background-image: -webkit-linear-gradient(top, #d2d2d2, #cdcdcd);
  cursor: default;
  -webkit-user-select: none;
  -webkit-app-region: drag;
}
.preferences-wrap .preference-header .preference-header-item {
  margin-left: 3px;
  margin-right: 3px;
  padding-left: 3px;
  padding-right: 3px;
  -webkit-app-region: no-drag;
  display: inline-block;
}
.preferences-wrap .preference-header .preference-header-item .phi-container {
  padding-left: 5px;
  padding-right: 5px;
  padding-top: 3px;
  text-align: center;
  height: 58px;
}
.preferences-wrap .preference-header .preference-header-item .phi-container .icon {
  height: 33px;
}
.preferences-wrap .preference-header .preference-header-item .phi-container .name {
  font-size: 12px;
  line-height: 22px;
  white-space: nowrap;
}
.preferences-wrap .preference-header .preference-header-item.active {
  background-color: rgba(0, 0, 0, 0.12);
  border-top-left-radius: 4px;
  border-top-right-radius: 4px;
}
