css:
.item-count-box {
  float: right;
  min-width: 26px;
  text-align: center;
  display: inline;
  font-size: 12px;
  padding: 1px 5px 1px 5px;
  margin-top: 3.5px;
  line-height: 20.9px;
  border-radius: 3px;
}
.sidebar-activity-transition-wrapper {
  order: 2;
  z-index: 2;
  overflow-y: auto;
}
.sidebar-activity-transition-wrapper.sidebar-activity-empty {
  position: absolute;
  bottom: 0;
  width: 100%;
}
.sidebar-activity {
  display: block;
  width: 100%;
  bottom: 0;
  background: #fdfdfd;
  font-size: 13.95px;
  color: rgba(35, 31, 32, 0.8);
  line-height: 20.9px;
  box-shadow: inset 0 1px 0 #dddddd;
  transition: height 0.4s;
  transition-delay: 2s;
}
.sidebar-activity:hover {
  cursor: default;
}
.sidebar-activity .item {
  border-bottom: 1px solid #dddddd;
  position: relative;
  opacity: 1;
}
.sidebar-activity .item:hover {
  cursor: default;
}
.sidebar-activity .item .inner {
  padding: 12px 12px 12px 12px;
}
.sidebar-activity .item .count {
  color: rgba(35, 31, 32, 0.5);
  float: right;
}
.sidebar-activity .item .btn {
  display: block;
  text-align: center;
  margin-top: 4px;
  margin-bottom: 4px;
  font-size: 13.95px;
}
.sidebar-activity .item .progress-track {
  display: block;
  height: 3px;
  font-size: 0;
  background: rgba(0, 0, 0, 0.1);
}
.sidebar-activity .item .progress-track .progress {
  transition: width 0.4s;
  height: 3px;
  background-color: #009ec4;
}
.sidebar-activity .item .account-detail-area {
  max-height: 0;
  overflow: hidden;
  transition: max-height 0.2s;
}
.sidebar-activity .item.expanded-sync .account-detail-area {
  max-height: 1000px;
}
.sidebar-activity .item.expanded-sync .account.inner {
  border-top: 1px solid rgba(0, 0, 0, 0.1);
}
.sidebar-activity .item h2 {
  font-size: 14px;
  margin: 0;
  overflow: hidden;
  text-overflow: ellipsis;
  font-weight: 500;
}
.sidebar-activity .item h3 {
  font-size: 14px;
  margin: 10px 0 4px 0;
}
.sidebar-activity .item .amount {
  margin-top: 2px;
  font-size: 12px;
  color: rgba(35, 31, 32, 0.8);
}
.sidebar-activity .item .close-expanded {
  display: block;
  margin: 0 12px 10px 12px;
}
.sidebar-activity .item .complete .progress-track .progress {
  background-color: #5cb346;
}
.sidebar-activity.sidebar-activity-error .progress {
  background-color: #d9534f;
}
.activity-opacity-enter {
  opacity: 0;
  transition: opacity 0.125s ease-out;
}
.activity-opacity-enter.activity-opacity-enter-active {
  opacity: 1;
}
.activity-opacity-leave {
  opacity: 1;
  transition: opacity 0.125s ease-in;
  transition-delay: 0.5s;
}
.activity-opacity-leave.activity-opacity-leave-active {
  transition-delay: 0.5s;
  opacity: 0;
}
.notifications-sticky {
  width: 100%;
}
.notifications-sticky .notification-info {
  background-color: #009ec4;
}
.notifications-sticky .notification-developer {
  background-color: #615396;
}
.notifications-sticky .notification-error {
  background-color: #f15f4b;
  border-color: #f15f4b;
  color: #d9534f;
}
.notifications-sticky .notification-success {
  border-color: #00ac6f;
}
.notifications-sticky .notifications-sticky-item {
  display: block;
  font-size: 15px;
  line-height: 1.5;
  padding: 6px 12px 6px 12px;
  color: #ffffff;
  border-top: 1px solid rgba(0, 0, 0, 0.25);
  border-bottom: 1px solid rgba(0, 0, 0, 0.25);
}
.notifications-sticky .notifications-sticky-item a {
  float: right;
  font-size: 13px;
  background-color: rgba(255, 255, 255, 0.2);
  color: #ffffff;
  border-radius: 4px;
  padding: 1px;
  padding-left: 6px;
  padding-right: 6px;
  margin-left: 10px;
  border-top: 1px solid transparent;
  border-bottom: none;
}
.notifications-sticky .notifications-sticky-item a:hover {
  background-color: rgba(0, 0, 0, 0.2);
  border-top: 1px solid rgba(0, 0, 0, 0.35);
  text-decoration: none;
  color: #ffffff;
}
.notifications-sticky .notifications-sticky-item i {
  margin-right: 12px;
}
