}
ul ul,
ol ul,
ul ol,
ol ol {
  margin-bottom: 0;
}
.list-unstyled {
  padding-left: 0;
  list-style: none;
}
.list-inline {
  padding-left: 0;
  list-style: none;
  margin-left: -5px;
}
.list-inline > li {
  display: inline-block;
  padding-left: 5px;
  padding-right: 5px;
}
dl {
  margin-top: 0;
  margin-bottom: 22px;
}
dt,
dd {
  line-height: 1.5;
}
dt {
  font-weight: bold;
}
dd {
  margin-left: 0;
}
abbr[title],
abbr[data-original-title] {
  cursor: help;
  border-bottom: 1px dotted #77828d;
}
.initialism {
  font-size: 90%;
  text-transform: uppercase;
}
blockquote {
  padding: 11px 22px;
  margin: 0 0 22px;
  border-left: 5px solid #f6f7f8;
}
blockquote p:last-child,
blockquote ul:last-child,
blockquote ol:last-child {
  margin-bottom: 0;
}
blockquote footer,
blockquote small,
blockquote .small {
  display: block;
  font-size: 80%;
  line-height: 1.5;
  color: #77828d;
}
blockquote footer:before,
blockquote small:before,
blockquote .small:before {
  content: '\2014 \00A0';
}
.blockquote-reverse,
blockquote.pull-right {
  padding-right: 15px;
  padding-left: 0;
  border-right: 5px solid #f6f7f8;
  border-left: 0;
  text-align: right;
}
.blockquote-reverse footer:before,
blockquote.pull-right footer:before,
.blockquote-reverse small:before,
blockquote.pull-right small:before,
.blockquote-reverse .small:before,
blockquote.pull-right .small:before {
  content: '';
}
.blockquote-reverse footer:after,
blockquote.pull-right footer:after,
.blockquote-reverse small:after,
blockquote.pull-right small:after,
.blockquote-reverse .small:after,
blockquote.pull-right .small:after {
  content: '\00A0 \2014';
}
address {
  margin-bottom: 22px;
  font-style: normal;
  line-height: 1.5;
}
input[type="text"],
input[type="email"],
input[type="date"],
input[type="datetime"],
input[type="datetime-local"],
input[type="month"],
input[type="number"],
input[type="password"],
input[type="range"],
input[type="search"],
input[type="tel"],
input[type="time"],
input[type="url"] {
  width: 100%;
  padding-left: 5px;
  padding-right: 5px;
  font-size: 15px;
  line-height: 22px;
  font-weight: 400;
  background: #ffffff;
}
input[type="text"]:disabled,
input[type="email"]:disabled,
input[type="date"]:disabled,
input[type="datetime"]:disabled,
input[type="datetime-local"]:disabled,
input[type="month"]:disabled,
input[type="number"]:disabled,
input[type="password"]:disabled,
input[type="range"]:disabled,
input[type="search"]:disabled,
input[type="tel"]:disabled,
input[type="time"]:disabled,
input[type="url"]:disabled {
  background: rgba(255, 255, 255, 0.6);
}
input[type="text"].input-bordered,
input[type="email"].input-bordered,
input[type="date"].input-bordered,
input[type="datetime"].input-bordered,
input[type="datetime-local"].input-bordered,
input[type="month"].input-bordered,
input[type="number"].input-bordered,
input[type="password"].input-bordered,
input[type="range"].input-bordered,
input[type="search"].input-bordered,
input[type="tel"].input-bordered,
input[type="time"].input-bordered,
input[type="url"].input-bordered {
  border-radius: 4px;
  border: 1px solid #cccccc;
}
input[type="text"]:focus.input-bordered,
input[type="email"]:focus.input-bordered {
  box-shadow: 0 0 3px #419bf9;
}
button,
html input[type="button"] {
  cursor: default;
}
button:hover,
html input[type="button"]:hover,
button:active,
html input[type="button"]:active {
  cursor: default;
}
.btn {
  padding: 0.33em 1em;
  border-radius: 4px;
  border: 0;
  cursor: default;
  display: inline-block;
  color: #231f20;
  background: -webkit-gradient(linear, left top, left bottom, from(#ffffff), to(#f4f4f4));
  box-shadow: 0 0.5px 0 rgba(0, 0, 0, 0.15), 0 -0.5px 0 rgba(0, 0, 0, 0.15), 0.5px 0 0 rgba(0, 0, 0, 0.15), -0.5px 0 0 rgba(0, 0, 0, 0.15), 0 1px 1px rgba(0, 0, 0, 0.15);
  height: auto;
  line-height: 1;
  font-size: 15px;
}
.btn img.content-mask {
  background-color: #231f20;
}
.btn:active {
  cursor: default;
  background-color: #e8e8e8;
  box-shadow: 0 0.5px 0 rgba(0, 0, 0, 0.15), 0 -0.5px 0 rgba(0, 0, 0, 0.15), 0.5px 0 0 rgba(0, 0, 0, 0.15), -0.5px 0 0 rgba(0, 0, 0, 0.15), 0 -1px 1px rgba(0, 0, 0, 0.21);
}
.btn:focus {
  outline: none;
}
.btn.btn-smaller {
  font-size: 12px;
}
.btn.btn-small {
  font-size: 13.95px;
}
.btn.btn-large {
  font-size: 15.9px;
  padding: 0.5em 1.3em;
}
.btn.btn-larger {
  font-size: 18px;
  padding: 0.66em 1.6em;
}
.btn.btn-action {
  color: #231f20;
  background: #5cb346;
}
.btn.btn-action img.content-mask {
  background-color: #231f20;
}
.btn.btn-emphasis {
  position: relative;
  color: #ffffff;
  font-weight: 500;
  background: linear-gradient(to bottom, #6bb1f9 0%, #0a80ff 100%);
  box-shadow: none;
  border: 1px solid #3878fa;
}
.btn.btn-emphasis img.content-mask {
  background-color: #ffffff;
}
.btn.btn-emphasis:before {
  content: ' ';
  width: calc(100% + 2px);
  height: calc(100% + 2px);
  border-radius: 5px;
  padding: 0;
  top: -1px;
  left: -1px;
  position: absolute;
  z-index: -1;
  background: linear-gradient(to bottom, #4ca2f9 0%, #015cff 100%);
}
.btn.btn-emphasis:active {
  background-image: -webkit-gradient(linear, left top, left bottom, from(#296efa), to(#4783fb));
  box-shadow: 0 1px 1px rgba(0, 0, 0, 0.21);
}
.btn.btn-text {
  font-size: 13px;
  padding: 2px 14px 2px 7px;
}
.btn.btn-text .text {
  position: relative;
  top: 1px;
  margin-left: 6px;
}
.btn.btn-danger,
.btn .btn-destructive {
  color: #ffffff;
  background: #d9534f;
}
.btn.btn-danger img.content-mask,
.btn .btn-destructive img.content-mask {
  background-color: #ffffff;
}
.btn-toolbar {
  height: 24px;
  padding: 2px 13px;
}
.btn-toolbar.narrow {
  padding: 2px 9px;
}
.btn-gradient {
  background: -webkit-gradient(linear, left top, left bottom, from(#ffffff), to(#f4f4f4));
}
.btn-gradient:active {
  background: -webkit-gradient(linear, left top, left bottom, from(#e8e8e8), to(#dddddd));
}
.btn-disabled {
  background: rgba(255, 255, 255, 0.85);
  box-shadow: 0 0.5px 0 rgba(0, 0, 0, 0.15), 0 -0.5px 0 rgba(0, 0, 0, 0.15), 0.5px 0 0 rgba(0, 0, 0, 0.15), -0.5px 0 0 rgba(0, 0, 0, 0.15);
}
.btn-disabled:active {
  background: rgba(255, 255, 255, 0.85);
  box-shadow: 0 0.5px 0 rgba(0, 0, 0, 0.15), 0 -0.5px 0 rgba(0, 0, 0, 0.15), 0.5px 0 0 rgba(0, 0, 0, 0.15), -0.5px 0 0 rgba(0, 0, 0, 0.15);
}
.btn-icon {
  background: transparent;
  border: 0;
  box-shadow: none;
  color: rgba(35, 31, 32, 0.8);
  margin-right: 10px;
  outline: none !important;
  font-size: 20px;
}
.btn-icon img.content-mask {
  background-color: rgba(35, 31, 32, 0.8);
}
.btn-icon:last-child {
  margin-right: 0;
}
.btn-icon.inverse {
  color: #ffffff;
}
.btn-icon.inverse img.content-mask {
  background-color: #ffffff;
}
.btn-icon.inverse:hover {
  color: white;
}
.btn-icon.inverse:active {
  color: #ffffff;
}
.btn-icon.inverse:active img.content-mask {
  background-color: #ffffff;
}
.btn-icon:hover {
  cursor: default;
  color: #419bf9;
  box-shadow: none;
}
.btn-icon:hover img.content-mask {
  background-color: #419bf9;
}
.btn-icon:active {
  color: #3187e1;
  box-shadow: none;
}
.btn-icon:active img.content-mask {
  background-color: #3187e1;
}
html,
body {
  width: 100%;
  height: 100%;
  overflow: hidden;
  font-family: "Nylas-Pro", "Helvetica", sans-serif;
  font-size: 15px;
  line-height: 1.5;
  -webkit-font-smoothing: antialiased;
}
atom-workspace {
  display: block;
  height: 100%;
  overflow: hidden;
  position: relative;
  font-family: "Nylas-Pro", "Helvetica", sans-serif;
  background-color: #ffffff;
}
atom-workspace atom-workspace-axis.horizontal {
  display: -webkit-flex;
  height: 100%;
}
atom-workspace atom-workspace-axis.vertical {
  display: -webkit-flex;
  -webkit-flex: 1;
  -webkit-flex-flow: column;
}
.sheet-container {
  height: 100%;
}
.sheet {
  background-color: #ffffff;
}
.sheet-stack-enter {
  left: 30px;
  opacity: 0;
  transition: all 0.125s ease-out;
}
.sheet-stack-enter.sheet-stack-enter-active {
  left: 0;
  opacity: 1;
}
.sheet-stack-leave {
  left: 0;
  opacity: 1;
  transition: all 0.125s ease-in;
}
.sheet-stack-leave.sheet-stack-leave-active {
  left: 30px;
  opacity: 0;
}
.toolbar-menu-control {
  display: none;
}
.toolbar-window-controls {
  margin-top: 14px;
  margin-left: 7.5px;
  order: -1000;
  min-width: 72px;
  width: 72px;
  flex-grow: 0;
  flex-shrink: 0;
}
.toolbar-window-controls:hover button {
  background-position: 0 -12px;
}
.toolbar-window-controls button {
  -webkit-app-region: no-drag;
  display: inline-block;
  padding: 0;
  width: 12px;
  height: 12px;
  margin: 4px;
  float: left;
  background-color: transparent;
  background-repeat: no-repeat;
  background-position: 0 0;
  background-size: 12px 48px;
  border: 0;
}
.toolbar-window-controls button:active {
  background-position: 0 -24px;
}
.toolbar-window-controls .close {
  background-image: url("images/application-frame/close@1x.png");
}
.toolbar-window-controls .minimize {
  background-image: url("images/application-frame/minimize@1x.png");
}
.toolbar-window-controls .maximize {
  background-image: url("images/application-frame/maximize@1x.png");
}
@media (-webkit-min-device-pixel-ratio: 2) {
  .toolbar-window-controls .close {
    background-image: url("images/application-frame/close@2x.png");
  }
  .toolbar-window-controls .minimize {
    background-image: url("images/application-frame/minimize@2x.png");
  }
  .toolbar-window-controls .maximize {
    background-image: url("images/application-frame/maximize@2x.png");
  }
}
body.is-blurred .toolbar-window-controls button {
  background-position: 0 -36px;
}
body.is-blurred .sheet-toolbar-container {
  background-image: -webkit-linear-gradient(top, #f6f6f6, #f6f6f6);
}
body.is-blurred .sheet-toolbar-container .btn.btn-toolbar {
  background: none;
}
body.is-blurred .sheet-toolbar-container .btn.btn-toolbar img {
  opacity: 0.5;
}
body.is-blurred .sheet-toolbar-container .item-container .window-title {
  opacity: 0.5;
}
.sheet-toolbar-container {
  background-image: -webkit-linear-gradient(top, #e9e9e9, #d2d2d2);
  box-shadow: inset 0 1px 0 #f9f9f9;
}
.layout-mode-popout .sheet-toolbar {
  background: #ffffff;
  height: 35px;
  min-height: 35px;
  max-height: 35px;
}
.layout-mode-popout .toolbar-window-controls {
  margin-top: 7px;
}
.sheet-toolbar {
  position: relative;
  -webkit-app-region: drag;
  border-bottom: 1px solid #bbbbbb;
  width: 100%;
  height: 50px;
  min-height: 50px;
  max-height: 50px;
  z-index: 10;
}
.sheet-toolbar .item-container > * {
  -webkit-app-region: no-drag;
}
.sheet-toolbar .item-spacer {
  -webkit-app-region: drag;
}
.sheet-toolbar .item-compose {
  order: 101;
}
.sheet-toolbar .item-container .window-title {
  position: absolute;
  text-align: center;
  left: 50%;
  transform: translateX(-50%);
  -webkit-app-region: drag;
  line-height: 36px;
}
.sheet-toolbar .item-container .window-title:hover {
  cursor: default;
}
.sheet-toolbar .item-back {
  order: -999;
  padding-top: 5px;
  padding-left: 11.25px;
  flex-grow: 0;
  flex-shrink: 0;
}
.sheet-toolbar .item-back img.content-mask {
  background-color: #434648;
}
.sheet-toolbar .item-back .item-back-title {
  cursor: default;
  color: #434648;
  margin: 0;
  font-size: 18.75px;
  font-weight: 400;
  vertical-align: middle;
  display: inline-block;
}
.sheet-toolbar .toolbar-menu-control {
  order: 10000;
  padding-top: 5px;
}
.sheet-toolbar .toolbar-menu-control .btn {
  margin-left: 0;
}
.sheet-toolbar .toolbar-menu-control img.content-mask {
  background-color: #434648;
}
.sheet-toolbar .btn-toolbar {
  margin-top: 11.25px;
  margin-left: 11.25px;
  margin-right: 0;
  flex-shrink: 0;
  height: 28px;
}
.sheet-toolbar .btn-toolbar:only-of-type {
  margin-right: 11.25px;
}
.sheet-toolbar-enter {
  opacity: 0;
  transition: opacity 0.125s ease-out;
}
.sheet-toolbar-enter.sheet-toolbar-enter-active {
  opacity: 1;
}
.sheet-toolbar-leave {
  opacity: 1;
  transition: opacity 0.125s ease-in;
}
.sheet-toolbar-leave.sheet-toolbar-leave-active {
  opacity: 0;
}
.flexbox-handle-horizontal {
  width: 6px;
  top: 0;
  bottom: 0;
  z-index: 2;
  position: absolute;
  cursor: col-resize;
}
.flexbox-handle-horizontal div {
  height: 100%;
  border-right: 1px solid #dddddd;
}
.flexbox-handle-horizontal.flexbox-handle-right {
  right: -4px;
  padding-right: 3px;
}
.flexbox-handle-horizontal.flexbox-handle-left {
  left: -3px;
  padding-right: 2px;
