css:
.empty-state {
  position: absolute;
  display: none;
  left: 0;
  top: 0;
  right: 0;
  bottom: 0;
  z-index: 1002;
  overflow: hidden;
}
.empty-state > div {
  opacity: 0;
  -webkit-transition: opacity 1s ease-out;
  width: 100%;
  height: 100%;
}
.empty-state .generic {
  text-align: center;
}
.empty-state .generic .message {
  color: rgba(35, 31, 32, 0.5);
  font-size: 28px;
  font-weight: 300;
  text-align: center;
  top: 45%;
  left: 50%;
  width: 80%;
  transform: translate(-50%, -50%);
  position: absolute;
  white-space: pre-line;
}
.empty-state .quotes {
  min-width: 840px;
  min-height: 650px;
  position: absolute;
  transform: translate(-50%, -50%);
  left: 50%;
  top: 50%;
}
.empty-state .quotes .message {
  opacity: 0;
  -webkit-user-select: text;
  -webkit-transition: opacity 1s ease-in;
  -webkit-transition-delay: 0.6s;
  top: 45%;
  left: 50%;
  transform: translate(-50%, -50%);
  width: 50%;
  min-width: 500px;
  position: absolute;
  color: rgba(35, 31, 32, 0.5);
  font-size: 28px;
  font-weight: 300;
  text-align: center;
  white-space: pre-line;
}
.empty-state .quotes .message .byline {
  padding-top: 25px;
  font-size: 14px;
  color: rgba(35, 31, 32, 0.5);
}
.empty-state .quotes .top-left {
  -webkit-transition: all 1s ease-out;
  position: absolute;
  top: -10px;
  left: -20px;
}
.empty-state .quotes .top-right {
  -webkit-transition: all 1s ease-out;
  position: absolute;
  top: -10px;
  right: -20px;
}
.empty-state .quotes .bottom-left {
  -webkit-transition: all 1s ease-out;
  position: absolute;
  bottom: -10px;
  left: -20px;
}
.empty-state .quotes .bottom-right {
  -webkit-transition: all 1s ease-out;
  position: absolute;
  bottom: -10px;
  right: -20px;
}
.empty-state.visible {
  display: block;
}
.empty-state.active > div {
  opacity: 1;
}
.empty-state.active > div .message {
  opacity: 1;
}
.empty-state.active > div .top-left {
  top: 0;
  left: 0;
}
.empty-state.active > div .top-right {
  top: 0;
  right: 0;
}
.empty-state.active > div .bottom-left {
  bottom: 0;
  left: 0;
}
.empty-state.active > div .bottom-right {
  bottom: 0;
  right: 0;
}
