css:
.item-count-box {
  float: right;
  min-width: 26px;
  text-align: center;
  display: inline;
  font-size: 12px;
  padding: 1px 5px 1px 5px;
  margin-top: 3.5px;
  line-height: 20.9px;
  border-radius: 3px;
}
.tag-picker .menu .content-container {
  height: 250px;
  overflow-y: scroll;
}
.sheet-toolbar .message-toolbar-items {
  order: 200;
  flex-grow: 0;
  flex-shrink: 0;
}
.sheet-toolbar .message-toolbar-arrow.down {
  order: 201;
  margin-right: 0;
  padding-top: 6px;
  margin-left: 22.5px;
}
.sheet-toolbar .message-toolbar-arrow.up {
  order: 202;
  padding-top: 6px;
  margin-right: 11.25px;
}
.sheet-toolbar .message-toolbar-arrow.disabled {
  opacity: 0.3;
}
.mode-split .message-nav-title {
  display: none;
}
.hide-sidebar-button {
  font-size: 13.95px;
  color: rgba(35, 31, 32, 0.8);
  margin-left: 15px;
  cursor: default;
  -webkit-user-select: none;
}
.hide-sidebar-button .img-wrap {
  margin-right: 7.5px;
  position: relative;
  top: -1px;
}
.hide-sidebar-button img {
  background: rgba(35, 31, 32, 0.8);
}
#message-list {
  display: flex;
  flex-direction: column;
  position: relative;
  background: #f6f6f6;
  width: 100%;
  height: 100%;
  min-height: 100%;
  padding: 0;
  order: 2;
}
#message-list .message-subject-wrap {
  width: calc(100% - 12px);
  max-width: 800px;
  margin: 11px auto 10px auto;
  padding-left: 20px;
  -webkit-user-select: text;
  line-height: 28.62px;
}
#message-list .mail-important-icon {
  margin-right: 7.5px;
  margin-bottom: 2px;
}
#message-list .message-subject {
  font-size: 15.9px;
  color: #231f20;
  margin-right: 15px;
}
#message-list .message-list-headers {
  margin: 0 auto;
  width: 100%;
  max-width: 800px;
  display: block;
}
#message-list .message-list-headers .participants .contact-chip {
  display: inline-block;
}
#message-list .message-list-notification-bars {
  display: block;
}
#message-list .messages-wrap {
  flex: 1;
  opacity: 0;
  transition: opacity 0s;
}
#message-list .messages-wrap.ready {
  opacity: 1;
  transition: opacity .1s linear;
}
#message-list .minified-bundle + .message-item-wrap {
  margin-top: 0;
}
#message-list .message-item-wrap {
  transition: height 0.1s;
  position: relative;
  max-width: 800px;
  width: calc(100% - 12px);
  margin: 0 auto;
  padding: 6px 0;
}
#message-list .message-item-wrap:last-child {
  padding-bottom: 30px;
}
#message-list .message-item-wrap .message-item-white-wrap {
  background: #ffffff;
  border: 0;
  box-shadow: 0 0 0.5px rgba(0, 0, 0, 0.28), 0 1px 1.5px rgba(0, 0, 0, 0.08);
  border-radius: 4px;
}
#message-list .message-item-wrap.before-reply-area {
  padding-bottom: 0;
}
#message-list .message-item-wrap.collapsed .message-item-white-wrap {
  background-color: #fafafa;
  padding-top: 19px;
  padding-bottom: 8px;
  margin-bottom: 0;
}
#message-list .message-item-wrap.collapsed + .minified-bundle {
  margin-top: -6px;
}
#message-list .message-item-wrap.collapsed .message-item-area {
  padding-bottom: 10px;
  display: flex;
  flex-direction: row;
  font-size: 13.95px;
}
#message-list .message-item-wrap.collapsed .message-item-area .collapsed-snippet {
  flex: 1;
  white-space: nowrap;
  text-overflow: ellipsis;
  overflow: hidden;
  cursor: default;
  color: rgba(35, 31, 32, 0.5);
}
#message-list .message-item-wrap.collapsed .message-item-area .collapsed-attachment {
  width: 15px;
  height: 15px;
  background-size: 15px;
  background-repeat: no-repeat;
  background-position: center;
  padding: 12px;
  margin-left: 0.5em;
  background-image: url(../static/images/message-list/icon-attachment-@2x.png);
  position: relative;
  top: -2px;
}
#message-list .message-item-wrap.collapsed .message-item-area .collapsed-from {
  font-weight: 600;
  color: rgba(35, 31, 32, 0.5);
  margin-right: 1em;
}
#message-list .message-item-wrap.collapsed .message-item-area .collapsed-timestamp {
  margin-left: 0.5em;
  color: rgba(35, 31, 32, 0.5);
}
#message-list .collapsed-participants {
  padding: 10px 10px 10px 0;
}
#message-list .message-item-divider {
  border: 0;
  border-top: 2px solid #dddddd;
  height: 3px;
  background: #f6f6f6;
  border-bottom: 1px solid #e6e6e6;
  margin: 0;
}
#message-list .message-item-divider.collapsed {
  height: 0;
  border-bottom: 0;
}
#message-list .minified-bundle {
  margin-right: 6px;
  margin-left: 6px;
  position: relative;
}
#message-list .minified-bundle .num-messages {
  position: absolute;
  top: 50%;
  left: 50%;
  margin-left: -80px;
  margin-top: -12px;
  border-radius: 15px;
  border: 1px solid #dddddd;
  width: 160px;
  text-align: center;
  color: rgba(35, 31, 32, 0.5);
  z-index: 2;
  background: #ffffff;
}
#message-list .minified-bundle .num-messages:hover {
  cursor: default;
}
#message-list .minified-bundle .msg-lines {
  max-width: 800px;
  margin: 0 auto;
  width: 100%;
}
#message-list .minified-bundle .msg-line {
  border-radius: 4px 4px 0 0;
  position: relative;
  border-top: 1px solid #dddddd;
  border-right: 1px solid #dddddd;
  border-left: 1px solid #dddddd;
  background-color: #fafafa;
  box-shadow: 0 1px 1px rgba(0, 0, 0, 0.1);
}
#message-list .message-header {
  position: relative;
  font-size: 13.95px;
  padding-bottom: 0;
  padding-top: 19px;
}
#message-list .message-header.pending .message-actions-wrap {
  width: 0;
  opacity: 0;
}
#message-list .message-header.pending .pending-spinner {
  opacity: 1;
}
#message-list .message-header .pending-spinner {
  transition: opacity 100ms;
  transition-delay: 50ms, 0ms;
  transition-timing-function: ease-in;
  opacity: 0;
}
#message-list .message-header .header-row {
  margin-top: 0.5em;
  color: rgba(35, 31, 32, 0.5);
}
#message-list .message-header .header-row .header-label {
  float: left;
  display: block;
  font-weight: 400;
  margin-left: 0;
}
#message-list .message-header .message-actions-wrap {
  transition: opacity 100ms, width 150ms;
  transition-delay: 50ms, 0ms;
  transition-timing-function: ease-in-out;
  width: 98px;
  height: 32px;
  opacity: 1;
  text-align: right;
}
#message-list .message-header .message-actions-wrap .button-dropdown {
  width: 76px;
}
#message-list .message-header .message-actions-ellipsis {
  display: block;
  float: left;
}
#message-list .message-header .message-actions {
  display: inline-block;
  height: 23px;
  border: 1px solid #ececec;
  border-radius: 11px;
  z-index: 4;
  margin-top: 0.35em;
  margin-left: 0.5em;
  text-align: center;
}
#message-list .message-header .message-actions .btn-icon {
  opacity: 0.75;
  padding: 0 7.5px;
  height: 20px;
  line-height: 10px;
  border-radius: 0;
  border-right: 1px solid #ececec;
  margin: 0;
}
#message-list .message-header .message-actions .btn-icon:last-child {
  border-right: 0;
}
#message-list .message-header .message-actions .btn-icon:active {
  background: transparent;
}
#message-list .message-header .message-time {
  padding-top: 5px;
  z-index: 2;
  position: relative;
  display: inline-block;
  min-width: 125px;
}
#message-list .message-header .msg-actions-tooltip {
  display: inline-block;
  margin-left: 1em;
}
#message-list .message-header .message-participants {
  z-index: 1;
  position: relative;
}
#message-list .message-header .message-time,
#message-list .message-header .message-indicator {
  color: rgba(35, 31, 32, 0.5);
}
#message-list .message-header .message-header-right {
  z-index: 4;
  position: relative;
  top: -5px;
  float: right;
  text-align: right;
  display: flex;
}
#message-list .message-item-area {
  width: 100%;
  max-width: 800px;
  margin: 0 auto;
  padding: 0 20px 15px 20px;
}
#message-list .message-item-area iframe {
  width: 100%;
  border: 0;
  padding: 0;
  margin-top: 5px;
  overflow: auto;
}
#message-list .collapse-region {
  width: calc(100% - 30px);
  height: 56px;
  position: absolute;
  top: 0;
}
#message-list .header-toggle-control {
  z-index: 3;
  position: absolute;
  top: 0;
  left: -13px;
}
#message-list .header-toggle-control.inactive {
  display: none;
}
#message-list .header-toggle-control img {
  background: rgba(35, 31, 32, 0.5);
}
#message-list .message-item-wrap:hover .header-toggle-control.inactive {
  display: block;
}
#message-list .footer-reply-area-wrap {
  overflow: hidden;
  width: calc(100% - 12px);
  max-width: 800px;
  margin: -3px auto 30px auto;
  position: relative;
  z-index: 2;
  border: 0;
  box-shadow: 0 0 0.5px rgba(0, 0, 0, 0.28), 0 1px 1.5px rgba(0, 0, 0, 0.08);
  border-top: 1px dashed #dddddd;
  border-radius: 0 0 4px 4px;
  background: #ffffff;
  color: rgba(35, 31, 32, 0.5);
}
#message-list .footer-reply-area-wrap img.content-mask {
  background-color: rgba(35, 31, 32, 0.5);
}
#message-list .footer-reply-area-wrap:hover {
  cursor: default;
}
#message-list .footer-reply-area-wrap .footer-reply-area {
  width: 100%;
  max-width: 800px;
  margin: 0 auto;
  padding: 20px 30px;
}
#message-list .footer-reply-area-wrap .reply-text {
  display: inline-block;
  margin-left: 0.5em;
  position: relative;
  top: 2px;
}
.attachments-area {
  padding-top: 15px;
  margin-left: -15px;
  margin-right: -15px;
  cursor: default;
}
.pending .message-participants {
  padding-left: 34px;
}
.message-participants {
  transition: padding-left 150ms;
  transition-timing-function: ease-in-out;
}
.message-participants.collapsed:hover {
  cursor: default;
}
.message-participants .from-contact {
  font-weight: 600;
  color: #231f20;
}
.message-participants .from-label,
.message-participants .to-label,
.message-participants .cc-label,
.message-participants .bcc-label,
.message-participants .subject-label {
  color: rgba(35, 31, 32, 0.5);
}
.message-participants .to-label,
.message-participants .cc-label,
.message-participants .bcc-label,
.message-participants .subject-label {
  margin-left: 15px;
}
.message-participants .to-contact,
.message-participants .cc-contact,
.message-participants .bcc-contact,
.message-participants .to-everyone {
  color: rgba(35, 31, 32, 0.5);
}
.message-participants .expanded-participants {
  position: relative;
  padding-right: 1.2em;
}
.message-participants .expanded-participants .participant {
  display: inline-block;
  margin-right: 0.5em;
}
.message-participants .expanded-participants .participant-type {
  margin-top: 0.5em;
}
.message-participants .expanded-participants .participant-type:first-child {
  margin-top: 0;
}
.message-participants .expanded-participants .from-label,
.message-participants .expanded-participants .to-label,
.message-participants .expanded-participants .cc-label,
.message-participants .expanded-participants .bcc-label,
.message-participants .expanded-participants .subject-label {
  float: left;
  display: block;
  font-weight: 400;
  margin-left: 0;
}
.message-participants .expanded-participants .from-contact,
.message-participants .expanded-participants .subject {
  font-weight: 600;
}
.message-participants .expanded-participants .to-label,
.message-participants .expanded-participants .cc-label {
  margin-right: 0.5em;
}
.message-participants .expanded-participants .bcc-label {
  margin-right: 0;
}
.message-participants .expanded-participants .participant-primary {
  color: rgba(35, 31, 32, 0.5);
}
.message-participants .expanded-participants .participant-secondary {
  color: rgba(35, 31, 32, 0.5);
}
.message-participants .expanded-participants .from-contact .participant-primary {
  color: #231f20;
}
.message-participants .expanded-participants .from-contact .participant-secondary {
  color: #231f20;
}
.sidebar-section {
  opacity: 0;
  padding: 15px;
  cursor: default;
}
.sidebar-section.visible {
  transition: opacity 0.1s ease-out;
  opacity: 1;
}
.sidebar-section h2 {
  font-size: 11px;
  font-weight: 600;
  text-transform: uppercase;
  color: rgba(35, 31, 32, 0.5);
  border-bottom: 1px solid #dddddd;
  margin: 2em 0 1em 0;
}
.sidebar-section .sidebar-contact-list {
  min-height: 120px;
}
.sidebar-section .sidebar-contact-list .contact {
  color: rgba(35, 31, 32, 0.8);
  font-size: 12px;
}
.sidebar-section .sidebar-contact-list .contact.selected {
  font-weight: 600;
}
.column-MessageListSidebar {
  background-color: #fdfdfd;
  overflow: auto;
  border-left: 1px solid #dddddd;
}
.column-MessageListSidebar .flexbox-handle-horizontal div {
  border-right: 0;
  width: 1px;
}
