css:
.item-count-box {
  float: right;
  min-width: 26px;
  text-align: center;
  display: inline;
  font-size: 12px;
  padding: 1px 5px 1px 5px;
  margin-top: 3.5px;
  line-height: 20.9px;
  border-radius: 3px;
}
button,
html input[type="button"] {
  cursor: default;
}
button:hover,
html input[type="button"]:hover,
button:active,
html input[type="button"]:active {
  cursor: default;
}
.btn {
  padding: 0.33em 1em;
  border-radius: 4px;
  border: 0;
  cursor: default;
  display: inline-block;
  color: #231f20;
  background: -webkit-gradient(linear, left top, left bottom, from(#ffffff), to(#f4f4f4));
  box-shadow: 0 0.5px 0 rgba(0, 0, 0, 0.15), 0 -0.5px 0 rgba(0, 0, 0, 0.15), 0.5px 0 0 rgba(0, 0, 0, 0.15), -0.5px 0 0 rgba(0, 0, 0, 0.15), 0 1px 1px rgba(0, 0, 0, 0.15);
  height: auto;
  line-height: 1;
  font-size: 15px;
}
.btn img.content-mask {
  background-color: #231f20;
}
.btn:active {
  cursor: default;
  background-color: #e8e8e8;
  box-shadow: 0 0.5px 0 rgba(0, 0, 0, 0.15), 0 -0.5px 0 rgba(0, 0, 0, 0.15), 0.5px 0 0 rgba(0, 0, 0, 0.15), -0.5px 0 0 rgba(0, 0, 0, 0.15), 0 -1px 1px rgba(0, 0, 0, 0.21);
}
.btn:focus {
  outline: none;
}
.btn.btn-smaller {
  font-size: 12px;
}
.btn.btn-small {
  font-size: 13.95px;
}
.btn.btn-large {
  font-size: 15.9px;
  padding: 0.5em 1.3em;
}
.btn.btn-larger {
  font-size: 18px;
  padding: 0.66em 1.6em;
}
.btn.btn-action {
  color: #231f20;
  background: #5cb346;
}
.btn.btn-action img.content-mask {
  background-color: #231f20;
}
.btn.btn-emphasis {
  position: relative;
  color: #ffffff;
  font-weight: 500;
  background: linear-gradient(to bottom, #6bb1f9 0%, #0a80ff 100%);
  box-shadow: none;
  border: 1px solid #3878fa;
}
.btn.btn-emphasis img.content-mask {
  background-color: #ffffff;
}
.btn.btn-emphasis:before {
  content: ' ';
  width: calc(100% + 2px);
  height: calc(100% + 2px);
  border-radius: 5px;
  padding: 0;
  top: -1px;
  left: -1px;
  position: absolute;
  z-index: -1;
  background: linear-gradient(to bottom, #4ca2f9 0%, #015cff 100%);
}
.btn.btn-emphasis:active {
  background-image: -webkit-gradient(linear, left top, left bottom, from(#296efa), to(#4783fb));
  box-shadow: 0 1px 1px rgba(0, 0, 0, 0.21);
}
.btn.btn-text {
  font-size: 13px;
  padding: 2px 14px 2px 7px;
}
.btn.btn-text .text {
  position: relative;
  top: 1px;
  margin-left: 6px;
}
.btn.btn-danger,
.btn .btn-destructive {
  color: #ffffff;
  background: #d9534f;
}
.btn.btn-danger img.content-mask,
.btn .btn-destructive img.content-mask {
  background-color: #ffffff;
}
.btn-toolbar {
  height: 24px;
  padding: 2px 13px;
}
.btn-toolbar.narrow {
  padding: 2px 9px;
}
.btn-gradient {
  background: -webkit-gradient(linear, left top, left bottom, from(#ffffff), to(#f4f4f4));
}
.btn-gradient:active {
  background: -webkit-gradient(linear, left top, left bottom, from(#e8e8e8), to(#dddddd));
}
.btn-disabled {
  background: rgba(255, 255, 255, 0.85);
  box-shadow: 0 0.5px 0 rgba(0, 0, 0, 0.15), 0 -0.5px 0 rgba(0, 0, 0, 0.15), 0.5px 0 0 rgba(0, 0, 0, 0.15), -0.5px 0 0 rgba(0, 0, 0, 0.15);
}
.btn-disabled:active {
  background: rgba(255, 255, 255, 0.85);
  box-shadow: 0 0.5px 0 rgba(0, 0, 0, 0.15), 0 -0.5px 0 rgba(0, 0, 0, 0.15), 0.5px 0 0 rgba(0, 0, 0, 0.15), -0.5px 0 0 rgba(0, 0, 0, 0.15);
}
.btn-icon {
  background: transparent;
  border: 0;
  box-shadow: none;
  color: rgba(35, 31, 32, 0.8);
  margin-right: 10px;
  outline: none !important;
  font-size: 20px;
}
.btn-icon img.content-mask {
  background-color: rgba(35, 31, 32, 0.8);
}
.btn-icon:last-child {
  margin-right: 0;
}
.btn-icon.inverse {
  color: #ffffff;
}
.btn-icon.inverse img.content-mask {
  background-color: #ffffff;
}
.btn-icon.inverse:hover {
  color: white;
}
.btn-icon.inverse:active {
  color: #ffffff;
}
.btn-icon.inverse:active img.content-mask {
  background-color: #ffffff;
}
.btn-icon:hover {
  cursor: default;
  color: #419bf9;
  box-shadow: none;
}
.btn-icon:hover img.content-mask {
  background-color: #419bf9;
}
.btn-icon:active {
  color: #3187e1;
  box-shadow: none;
}
.btn-icon:active img.content-mask {
  background-color: #3187e1;
}
.composer-inner-wrap {
  position: relative;
  height: 100%;
  display: flex;
  flex-direction: column;
}
.composer-inner-wrap .composer-drop-cover {
  position: absolute;
  top: 0;
  right: 0;
  bottom: 0;
  left: 0;
  z-index: 1000;
  background: rgba(255, 255, 255, 0.7);
  border-radius: 4px;
  border: 4px dashed #a5adb4;
  text-align: center;
  line-height: 2.3em;
}
.composer-inner-wrap .composer-drop-cover .centered {
  position: absolute;
  left: 50%;
  top: 50%;
  transform: translate(-50%, -50%);
  color: #8a939d;
  font-weight: 500;
  font-size: 1.1em;
}
.composer-inner-wrap .composer-drop-cover .centered img {
  margin: auto;
  display: block;
  margin-bottom: 20px;
  background-color: #8a939d;
}
.composer-inner-wrap .composer-action-bar-wrap {
  position: relative;
  width: 100%;
  background: transparent;
  border-bottom: 0;
}
.composer-inner-wrap .composer-action-bar-wrap .btn {
  height: 28px;
}
.composer-inner-wrap .composer-action-bar-wrap .composer-action-bar-content {
  display: flex;
  margin: 0 auto;
  flex-direction: row;
  max-width: 800px;
  padding: 15px;
}
.composer-inner-wrap .composer-action-bar-wrap .composer-action-bar-content > * {
  margin-left: 7.5px;
  margin-right: 7.5px;
}
.composer-inner-wrap .composer-content-wrap {
  padding: 0;
  flex: 1;
  display: flex;
  position: relative;
  flex-flow: column;
}
.composer-inner-wrap .composer-centered {
  display: flex;
  position: relative;
  flex-direction: column;
  flex: 1;
  width: 100%;
  max-width: 800px;
  margin: 0 auto;
  padding-top: 15px;
}
.composer-inner-wrap .text-actions {
  text-align: right;
  line-height: 1.4;
  position: relative;
  top: -3px;
}
.composer-inner-wrap .header-action {
  color: rgba(35, 31, 32, 0.5);
  font-size: 13.95px;
  padding-left: 1em;
}
.composer-inner-wrap .header-action img.content-mask {
  background-color: rgba(35, 31, 32, 0.5);
}
.composer-inner-wrap .header-action:hover {
  color: #419bf9;
  cursor: default;
}
.composer-inner-wrap .header-action:hover img.content-mask {
  background-color: #419bf9;
}
.composer-inner-wrap .composer-participant-actions {
  position: relative;
  float: right;
  z-index: 2;
  cursor: default;
  padding-right: 22.5px;
  padding-left: 15px;
  padding-top: 12px;
}
.composer-inner-wrap input,
.composer-inner-wrap textarea,
.composer-inner-wrap div[contenteditable] {
  color: #231f20;
  position: relative;
  display: block;
  background: inherit;
  width: 100%;
  resize: none;
  border: none;
}
.composer-inner-wrap .composer-field-label {
  color: rgba(35, 31, 32, 0.5);
  float: left;
  padding-top: 6px;
  display: block;
}
.composer-inner-wrap .collapsed-composer-participants {
  position: relative;
  margin: 0 23px;
  border-bottom: 1px solid #dddddd;
  flex-shrink: 0;
  color: rgba(35, 31, 32, 0.5);
  padding: 16px 0 10px 0;
}
.composer-inner-wrap .collapsed-composer-participants .collapsed-contact {
  padding-right: 0.25em;
  color: #231f20;
}
.composer-inner-wrap .collapsed-composer-participants .collapsed-contact:after {
  content: ",";
}
.composer-inner-wrap .collapsed-composer-participants .collapsed-contact:last-child:after {
  content: "";
}
.composer-inner-wrap .collapsed-composer-participants .num-remaining.token {
  color: #231f20;
  color: rgba(0, 0, 0, 0.6);
  padding-right: 12px;
  margin-left: 0;
  padding-top: 6px;
  padding-bottom: 6px;
}
.composer-inner-wrap .collapsed-composer-participants .num-remaining-wrap {
  position: absolute;
  right: 0;
  z-index: 2;
  top: 9px;
}
.composer-inner-wrap .collapsed-composer-participants .num-remaining-wrap .show-more-fade {
  position: absolute;
  width: 220px;
  height: 37px;
  right: 0;
  top: 0;
  background: linear-gradient(to right, rgba(255, 255, 255, 0) 0%, #ffffff 40%);
}
.composer-inner-wrap .compose-subject-wrap {
  position: relative;
  margin: 0 23px;
  border-bottom: 1px solid #dddddd;
  flex-shrink: 0;
}
.composer-inner-wrap .compose-subject-wrap input {
  display: inline-block;
  padding: 13px 0 9px 0;
  margin: 0 0 0 5px;
  min-width: 5em;
  background-color: transparent;
  border: none;
}
.composer-inner-wrap .compose-subject-wrap input {
  margin-left: 0;
}
.composer-inner-wrap .compose-subject-wrap input::-webkit-input-placeholder {
  color: rgba(35, 31, 32, 0.5);
}
.composer-inner-wrap .compose-body-scroll {
  position: initial;
}
.composer-inner-wrap .compose-body-scroll .scroll-region-content .scroll-region-content-inner {
  min-height: 100%;
  display: flex;
}
.composer-inner-wrap .compose-body {
  flex: 1;
  cursor: text;
  overflow: auto;
  position: relative;
  margin: 0 8px;
}
.composer-inner-wrap .compose-body .quoted-text-control {
  margin: 15px;
  margin-top: 0;
}
.composer-inner-wrap .compose-body div[contenteditable] {
  font-size: 15px;
  line-height: 1.4;
  min-height: 150px;
  padding: 15px;
  padding-top: 20px;
  padding-bottom: 0;
  margin-bottom: 30px;
}
.composer-inner-wrap .compose-body .contenteditable-container {
  width: 100%;
  position: relative;
}
.composer-inner-wrap .compose-body spelling.misspelled {
  background: linear-gradient(45deg, transparent, transparent 49%, #ff0000 49%, transparent 51%);
  background-size: 2px 2px;
  background-position: bottom;
  background-repeat-y: no-repeat;
}
.composer-inner-wrap .composer-footer-region {
  cursor: default;
}
.composer-inner-wrap .composer-footer-region:hover {
  cursor: default;
}
.composer-inner-wrap .attachments-area {
  padding: 0;
  margin: 0;
}
.composer-full-window {
  width: 100%;
  height: 100%;
}
.composer-full-window .composer-outer-wrap {
  width: 100%;
  height: 100%;
}
.composer-full-window .composer-inner-wrap .composer-action-bar-wrap {
  background: #fcfcfc;
  border-top: 1px solid #eaeaea;
  box-shadow: inset 0 1px 2px rgba(0, 0, 0, 0.03);
}
.composer-full-window .composer-inner-wrap .composer-action-bar-content {
  padding: 8px 0.5px;
}
.composer-full-window .composer-inner-wrap .compose-body {
  margin-bottom: 0;
  position: relative;
}
.composer-full-window .composer-inner-wrap .compose-body .contenteditable-container {
  flex: 1;
  width: 100%;
  position: relative;
}
.composer-full-window .composer-inner-wrap .compose-body .contenteditable-container div[contenteditable] {
  height: auto;
  flex: 1;
}
.composer-full-window .compose-body div[contenteditable] {
  min-height: 22px;
}
.composer-full-window .from-picker:hover {
  cursor: default;
}
#message-list .message-item-wrap .message-item-white-wrap.composer-outer-wrap {
  background: #fffcf5;
}
#message-list .message-item-wrap .message-item-white-wrap.composer-outer-wrap.focused {
  box-shadow: 0 0 0.5px rgba(0, 0, 0, 0.28), 0 1px 1.5px rgba(0, 0, 0, 0.08), 0 0 3px #419bf9;
  background-color: #ffffff;
}
.composer-participant-field {
  position: relative;
  padding: 7px 0 0 0;
  margin: 0 23px;
  flex-shrink: 0;
  border-bottom: 1px solid #dddddd;
  min-height: 49px;
}
.composer-participant-field .button-dropdown {
  margin-left: 10px;
}
.composer-participant-field .participant {
  white-space: nowrap;
  text-overflow: ellipsis;
  overflow: hidden;
}
.composer-participant-field .participant .participant-primary {
  font-weight: 600;
}
.composer-participant-field .participant .participant-secondary {
  color: rgba(35, 31, 32, 0.5);
}
.floating-toolbar {
  z-index: 10;
  position: absolute;
  background: #fff;
  box-shadow: 0 10px 20px rgba(0, 0, 0, 0.19), inset 0 0 1px rgba(0, 0, 0, 0.5);
  border-radius: 4px;
  color: #231f20;
  transition-duration: .15s;
  transition-property: opacity, margin;
  opacity: 0;
  visibility: hidden;
  margin-top: 3px;
}
.floating-toolbar.toolbar-visible {
  opacity: 1;
  visibility: visible;
  margin-top: 0;
}
.floating-toolbar .toolbar-pointer {
  position: absolute;
  width: 22.5px;
  height: 10px;
  background: transparent url('images/tooltip/tooltip-bg-pointer@2x.png') no-repeat;
  background-size: 22.5px 10.5px;
  margin-left: -11.2px;
}
.floating-toolbar.above .toolbar-pointer {
  transform: rotate(0deg);
  bottom: -9px;
}
.floating-toolbar.below .toolbar-pointer {
  transform: rotate(180deg);
  top: -9px;
}
.floating-toolbar .floating-toolbar-input {
  display: inline;
  width: calc(100% - 62px);
  color: #231f20;
}
.floating-toolbar .floating-toolbar-input.with-remove {
  width: calc(100% - 95px);
}
.floating-toolbar .btn {
  background: transparent;
  font-size: 16px;
  height: auto;
  border-radius: 0;
  padding: 0.375em 0.5em;
  margin: 0;
  color: #231f20;
  box-shadow: none;
}
.floating-toolbar .btn:first-child {
  padding-left: 0.75em;
}
.floating-toolbar .btn:last-child {
  padding-right: 0.75em;
}
.floating-toolbar .btn:hover,
.floating-toolbar .btn:active {
  color: #72b5fb;
  background: transparent;
}
.floating-toolbar .preview-btn-icon {
  position: relative;
  top: 1px;
  padding: 0 0.5em;
}
.floating-toolbar .toolbar-btn {
  width: 28.5px;
  height: 20.5px;
  margin: 7.5px 0;
  box-shadow: none;
  border: 0;
  border-right: 1px solid #dddddd;
  background: no-repeat;
  background-size: 12.5px 12.5px;
  background-position: 8px 4px;
}
.floating-toolbar .toolbar-btn:last-child {
  border-right: 0;
}
.floating-toolbar .toolbar-btn.btn-bold {
  background-image: url("images/composer/tooltip-bold-black@2x.png");
}
.floating-toolbar .toolbar-btn.btn-italic {
  background-image: url("images/composer/tooltip-italic-black@2x.png");
}
.floating-toolbar .toolbar-btn.btn-underline {
  background-image: url("images/composer/tooltip-underline-black@2x.png");
}
.floating-toolbar .toolbar-btn.btn-link {
  background-image: url("images/composer/tooltip-link-black@2x.png");
}
.floating-toolbar .toolbar-btn:hover {
  cursor: pointer;
  background: no-repeat;
  background-size: 12.5px 12.5px;
  background-position: 8px 4px;
}
.floating-toolbar .toolbar-btn:hover.btn-bold {
  background-image: url("images/composer/tooltip-bold-blue@2x.png");
}
.floating-toolbar .toolbar-btn:hover.btn-italic {
  background-image: url("images/composer/tooltip-italic-blue@2x.png");
}
.floating-toolbar .toolbar-btn:hover.btn-underline {
  background-image: url("images/composer/tooltip-underline-blue@2x.png");
}
.floating-toolbar .toolbar-btn:hover.btn-link {
  background-image: url("images/composer/tooltip-link-blue@2x.png");
}
