css:
@-webkit-keyframes fadein {
  from {
    opacity: 0;
  }
  to {
    opacity: 1;
  }
}
@-webkit-keyframes bounce {
  0% {
    transform: scale(1);
  }
  50% {
    transform: scale(1.3);
  }
  100% {
    transform: scale(1);
  }
}
@-webkit-keyframes fill {
  0% {
    opacity: 0;
    border-width: 68px;
  }
  25% {
    opacity: 1;
  }
  100% {
    opacity: 1;
    border-width: 0;
  }
}
.page-frame {
  background-color: #f6f7f8;
  -webkit-animation: fadein 0.8s;
  text-align: center;
  display: inline-block;
}
.page-frame h1 {
  font-weight: 100;
  font-size: 40pt;
  margin: 0;
}
.page-frame h2 {
  line-height: 1.3em;
}
.page-frame h4 {
  font-weight: 400;
  font-size: 20pt;
}
.page-frame iframe,
.page-frame webview {
  position: absolute;
  width: 100%;
  height: 100%;
  left: 0;
  top: 0;
  border: 0;
}
.page-frame .logo-container {
  width: 117px;
  height: 117px;
  display: inline-block;
  padding-top: 30px;
  box-sizing: content-box;
}
.page-frame .btn-add-account {
  width: 140px;
  transition: width 150ms ease-in-out;
}
.page-frame .btn-add-account-spinning {
  width: 190px;
}
.page-frame .btn-add-account-spinning img {
  vertical-align: top;
  margin-right: 5px;
}
.page-frame .prompt {
  color: #5D5D5D;
  font-size: 1.07em;
  font-weight: 300;
  margin-top: 20px;
  margin-bottom: 14px;
}
.page-frame form {
  padding-right: 25px;
  padding-left: 25px;
}
.page-frame input {
  display: block;
  padding: 10px;
  width: 100%;
  margin-bottom: 20px;
  background-color: #F5F5F5;
  text-align: center;
}
.page-frame .environment-selector {
  opacity: 0.1;
  width: 292px;
  margin: 0 auto 20px auto;
}
.page-frame .environment-selector select {
  width: 100%;
}
.page-frame .page-container {
  z-index: 10;
  position: absolute;
  top: 0;
  left: -50%;
  width: 200%;
}
.page-frame .page {
  margin: auto;
}
.page-frame .page.account-choose {
  padding-top: 45px;
}
.page-frame .page.account-setup {
  padding: 0 15px;
  width: 330px;
}
.page-frame .page.opaque {
  background-color: #f6f7f8;
}
.page-frame .page-gradient {
  position: absolute;
  top: 0;
  width: 100%;
  height: 200px;
  z-index: 5;
  transition: background 0.3s ease-in-out;
}
.page-frame .page-gradient.under {
  z-index: 0;
}
.page-frame .page-background {
  height: 100%;
  width: 100%;
  position: absolute;
  top: 0;
}
.page-frame .quit {
  position: fixed;
  z-index: 100;
  top: 1px;
  left: 6px;
}
.page-frame .back,
.page-frame .dismiss {
  position: fixed;
  top: 25px;
  left: 25px;
}
.page-frame .alpha-fade-enter {
  opacity: 0.01;
  transition: all 0.15s ease-out;
}
.page-frame .alpha-fade-enter.alpha-fade-enter-active {
  opacity: 1;
}
.page-frame .alpha-fade-leave {
  opacity: 1;
  transition: all 0.15s ease-in;
}
.page-frame .alpha-fade-leave.alpha-fade-leave-active {
  opacity: 0.01;
}
.page-frame .check {
  width: 136px;
  height: 136px;
  background: #e5e5e5;
  border-radius: 50%;
  position: absolute;
  top: 50%;
  left: 50%;
  transform: translateX(-50%) translateY(-50%);
  box-shadow: 0 -4px 4px 0 rgba(255, 255, 255, 0.75), inset 0 4px 4px 0 rgba(0, 0, 0, 0.08);
}
.page-frame .check:before {
  content: "";
  position: absolute;
  border-radius: 50%;
  top: 9%;
  left: 9%;
  right: 9%;
  bottom: 9%;
  box-shadow: 0 4px 4px 0 rgba(0, 0, 0, 0.08);
}
.page-frame .check:after {
  content: "";
  position: absolute;
  border-radius: 50%;
  top: 0;
  left: 0;
  right: 0;
  bottom: 0;
  opacity: 0;
  background-color: #60b247;
  border-width: 0;
  border-color: #e5e5e5;
  border-style: solid;
  -webkit-animation: fill 0.5s forwards;
  -webkit-animation-delay: 0.8s;
}
.page-frame .check .check-icon {
  position: absolute;
  z-index: 3;
  top: 31%;
  left: 28%;
  width: 45%;
  fill: white;
  -webkit-animation: bounce 0.4s forwards;
  -webkit-animation-delay: 1.9s;
}
.page-frame .errormsg {
  color: #A33;
  margin-bottom: 5px;
}
.page-frame form.settings {
  padding: 0 20px;
  padding-bottom: 20px;
}
.page-frame form.settings input {
  padding: 8px;
  display: inline-block;
  width: 100%;
  margin-bottom: 10px;
  background: #FFF;
  color: #333;
  text-align: left;
  border: 1px solid #AAA;
}
.page-frame form.settings input::-webkit-input-placeholder {
  color: #C6C6C6;
}
.page-frame form.settings input:disabled {
  background: rgba(255, 255, 255, 0.6);
}
.page-frame form.settings input[type=checkbox] {
  width: initial;
  margin-right: 5px;
}
.page-frame form.settings input.error {
  border: 1px solid #A33;
}
.page-frame form.settings label {
  display: inline-block;
  width: 100%;
  color: #888;
  text-align: left;
}
.page-frame form.settings label.half {
  width: 50%;
}
.page-frame form.settings label.checkbox {
  text-align: center;
}
.page-frame form.settings .btn {
  margin-top: 8px;
}
.page-frame .appearance-mode {
  background-color: #f7f9f9;
  border-radius: 10px;
  border: 1px solid #c6c7c7;
  text-align: center;
  flex: 1;
  padding: 9px;
  padding-top: 10px;
  margin: 10px;
  margin-top: 0;
}
.page-frame .appearance-mode img {
  background-color: #c6c7c7;
}
.page-frame .appearance-mode div {
  margin-top: 10px;
  text-transform: capitalize;
  cursor: default;
}
.page-frame .appearance-mode.active {
  border: 1px solid #3187e1;
  color: #3187e1;
}
.page-frame .appearance-mode.active img {
  background-color: #3187e1;
}
.page.account-choose {
  width: 388px;
  height: 688px;
}
.page.account-choose .caption {
  font-size: 17px;
  color: rgba(0, 0, 0, 0.56);
}
.page.account-choose .provider-name {
  font-size: 20px;
  font-weight: 300;
  color: rgba(0, 0, 0, 0.7);
}
.page.account-choose .provider {
  text-align: left;
  border-top: 1px solid rgba(0, 0, 0, 0.05);
  cursor: default;
  line-height: 70px;
}
.page.account-choose .provider .icon-container {
  width: 50px;
  height: 50px;
  display: inline-block;
  box-sizing: content-box;
  padding: 0 25px 0 25px;
  vertical-align: top;
}
.page.account-choose .provider:hover {
  background: rgba(255, 255, 255, 0.7);
}
.page.token-auth.token-auth-enabled {
  height: auto;
  padding-bottom: 25px;
}
.page.token-auth {
  width: 388px;
  min-height: 500px;
  position: relative;
  padding-top: 50px;
}
.page.token-auth img.logo.content-mask {
  background-color: rgba(255, 255, 255, 0.4);
}
.page.token-auth .caption {
  font-size: 17px;
  color: rgba(0, 0, 0, 0.56);
}
.page.token-auth .token-label {
  max-width: 75%;
}
.page.token-auth input {
  display: block;
  padding: 10px;
  width: 75%;
  margin: 0 auto 20px auto;
  background-color: #F5F5F5;
  text-align: center;
}
.page.token-auth input.error {
  border: 1px solid #A33;
}
.initial-package {
  display: block;
  margin: auto;
  margin-top: 10px;
  margin-bottom: 10px;
  width: 550px;
  padding: 15px;
  border: 1px solid #e1e1e1;
  text-align: left;
  cursor: default;
}
.initial-package img {
  float: left;
  margin: 12px;
  margin-left: 5px;
  margin-right: 20px;
}
.initial-package .name {
  font-size: 20px;
}
.initial-package .description {
  font-size: 14px;
  max-width: 500px;
}
.initial-package .install-container {
  width: 90px;
  float: right;
  text-align: right;
}
.initial-package .btn-install {
  display: inline-block;
  width: 70px;
  margin: 24px;
  margin-right: 7px;
  margin-left: 14px;
  height: 24px;
  color: #231f20;
  transition: width 150ms ease-in-out, color 150ms ease-in-out;
}
.initial-package .btn-install:after {
  content: "Install";
}
.initial-package .btn-install.installing {
  width: 32px;
  color: transparent;
  background: url('nylas://onboarding/assets/installing-spinner.gif') center no-repeat;
  background-size: 18px;
  box-shadow: none;
}
.initial-package .btn-install.installed {
  width: 32px;
  height: 32px;
  margin-top: 20px;
  color: transparent;
  background: url('nylas://onboarding/assets/green_check@2x.png') center no-repeat;
  background-size: 27px;
  box-shadow: none;
}
.initial-package .btn-install.installed:after,
.initial-package .btn-install.installing:after {
  content: "";
}
.btn-get-started .spinner {
  width: 16px;
  height: 16px;
  vertical-align: middle;
  display: inline-block;
  margin-right: 6px;
  background: url('nylas://onboarding/assets/get-started-spinner.gif') center no-repeat;
  background-size: 16px;
}
.welcome-page {
  width: 675px;
  height: 480px;
  display: flex;
  flex-direction: column;
}
@-webkit-keyframes slideIn {
  from {
    transform: translate3d(20, 0, 0);
    opacity: 0;
  }
  to {
    transform: translate3d(0, 0, 0);
    opacity: 1;
  }
}
.welcome-page a {
  color: rgba(255, 255, 255, 0.7);
  border-bottom: 1px solid rgba(255, 255, 255, 0.7);
}
.welcome-page a:hover {
  cursor: default;
  color: white;
  border-bottom: 1px solid white;
}
.welcome-page .steps-container {
  position: relative;
  flex: 1;
}
.welcome-page .step-wrap {
  position: absolute;
  top: 0;
  left: 0;
  right: 0;
  bottom: 0;
  transition: all 0.3s;
  z-index: 1;
  opacity: 0;
}
.welcome-page .step-wrap.active {
  opacity: 1;
  z-index: 3;
}
.welcome-page .footer {
  text-align: center;
  background: #ececec;
  border-top: 1px solid #d4d4d4;
}
.welcome-page .footer .btn-continue {
  font-size: 18px;
  font-weight: 300;
  margin: 20px 0;
  padding: 12px 0;
  width: 296px;
}
.welcome-page .footer .btn-back {
  color: rgba(0, 0, 0, 0.4);
  position: absolute;
  left: 20px;
  bottom: 30px;
}
.welcome-page .footer .btn-back:hover {
  color: rgba(0, 0, 0, 0.7);
  cursor: default;
}
.welcome-page p.hero-text {
  font-weight: 200;
  -webkit-font-smoothing: subpixel-antialiased;
}
.welcome-page p.sub-text {
  font-size: 17px;
  font-weight: 300;
}
.welcome-page .nylas-wash-light-bg {
  background-image: linear-gradient(to bottom, rgba(236, 236, 236, 0) 0%, #ececec 88%), linear-gradient(to right, rgba(213, 224, 0, 0.47) 0%, rgba(88, 182, 63, 0.47) 50%, rgba(12, 162, 163, 0.47) 100%);
  color: white;
}
.welcome-page .nylas-wash-bg {
  background: rgba(101, 191, 191, 0.44);
  background-image: linear-gradient(to right, rgba(213, 224, 0, 0.35) 0%, rgba(88, 182, 63, 0.35) 50%, rgba(12, 162, 163, 0.35) 100%);
  color: white;
}
.welcome-page .nylas-static-wash-bg {
  background-image: linear-gradient(to right, #a2d280 0%, #71c19a 50%, #59bbbb 100%);
  color: white;
}
.welcome-page .nylas-dark-bg {
  background: #3a444a;
  color: rgba(255, 255, 255, 0.7);
}
.welcome-page .nylas-dark-bg .sub-text {
  color: rgba(255, 255, 255, 0.5);
}
.welcome-page .nylas-blue-wash-bg {
  background-image: linear-gradient(to right, #4fba97 0%, #71c19a 50%, #22acbb 100%);
  color: white;
}
.welcome-page .step-0-wrap {
  background-image: linear-gradient(to right, #a2d280 0%, #71c19a 50%, #59bbbb 100%);
  color: white;
}
.welcome-page .step-0-wrap .hero-text {
  font-size: 34px;
  line-height: 41px;
}
.welcome-page .step-0-wrap img.icons.content-mask {
  background-color: rgba(255, 255, 255, 0.7);
}
.welcome-page .step-1-wrap {
  background: #3a444a;
  color: rgba(255, 255, 255, 0.7);
}
.welcome-page .step-1-wrap .sub-text {
  color: rgba(255, 255, 255, 0.5);
}
.welcome-page .step-1-wrap .hero-text {
  font-size: 29px;
}
.welcome-page .step-1-wrap .gear-outer-container {
  background: #69767f;
  border: 1px solid #161f25;
  width: 210px;
  height: 210px;
  margin: 20px auto;
  border-radius: 105px;
}
.welcome-page .step-1-wrap .gear-container {
  position: relative;
  background: #182025;
  border: 4px solid #161f25;
  width: 204px;
  height: 204px;
  margin: 3px auto;
  border-radius: 102px;
  overflow: hidden;
  -webkit-mask-image: url(data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAEAAAABCAIAAACQd1PeAAAAGXRFWHRTb2Z0d2FyZQBBZG9iZSBJbWFnZVJlYWR5ccllPAAAAA5JREFUeNpiYGBgAAgwAAAEAAGbA+oJAAAAAElFTkSuQmCC);
}
@keyframes rotate {
  from {
    transform: rotate(0deg);
  }
  to {
    transform: rotate(720deg);
  }
}
@keyframes rotate-2 {
  from {
    transform: rotate(7deg);
  }
  to {
    transform: rotate(727deg);
  }
}
.welcome-page .step-1-wrap.active .gear-large {
  animation: rotate 20s ease-in-out;
  animation-iteration-count: infinite;
}
.welcome-page .step-1-wrap.active .gear-large-0 {
  animation-name: rotate-2;
  animation-direction: reverse;
}
.welcome-page .step-1-wrap .gear-large {
  position: absolute;
}
.welcome-page .step-1-wrap .gear-large-0 {
  left: 50%;
  top: 50%;
  margin-left: -150px;
  margin-top: -150px;
  transform: rotate(7deg);
}
.welcome-page .step-1-wrap .gear-large-1 {
  left: -147px;
  top: -147px;
}
.welcome-page .step-1-wrap .gear-large-2 {
  right: -147px;
  top: -147px;
}
.welcome-page .step-1-wrap .gear-large-3 {
  left: 50%;
  margin-left: -148px;
  bottom: -226px;
}
.welcome-page .step-1-wrap .gear-small {
  position: absolute;
  top: 470px;
  left: 390px;
}
.welcome-page .step-1-wrap .wrench {
  position: absolute;
  right: 310px;
  top: 370px;
}
.welcome-page .step-2-wrap {
  background-image: linear-gradient(to right, #4fba97 0%, #71c19a 50%, #22acbb 100%);
  color: white;
}
.welcome-page .step-2-wrap .cell-wrap {
  width: 514px;
  margin: 30px auto 0 auto;
}
.welcome-page .step-2-wrap .cell {
  width: 242px;
  background: rgba(247, 251, 251, 0.25);
  border-radius: 10px;
  box-shadow: 0 1px 1px rgba(0, 0, 0, 0.09), inset 0 0 1px rgba(255, 255, 255, 0.15);
  padding: 30px 0;
}
.welcome-page .step-2-wrap .cell p {
  margin-top: 1em;
  color: rgba(255, 255, 255, 0.9);
  font-size: 17px;
  line-height: 24px;
}
.welcome-page .step-2-wrap .cell a {
  margin-top: 1em;
}
.welcome-page .step-2-wrap .hero-text {
  font-size: 27px;
}
.welcome-page .nav-bubbles {
  position: absolute;
  bottom: 14px;
  left: 50%;
  margin-left: -24px;
  display: flex;
  width: 48px;
}
.welcome-page .nav-bubble {
  margin: 4px;
  width: 8px;
  height: 8px;
  border-radius: 4px;
  background-color: rgba(255, 255, 255, 0.2);
}
.welcome-page .nav-bubble:hover {
  background-color: rgba(255, 255, 255, 0.4);
}
.welcome-page .nav-bubble.active {
  background-color: rgba(255, 255, 255, 0.7);
}
