css:
#tooltip-container {
  position: absolute;
  z-index: 999;
  top: 0;
  left: 0;
}
.tooltip-wrap {
  position: absolute;
  display: none;
  background: #fff;
  box-shadow: 0 10px 20px rgba(0, 0, 0, 0.19), inset 0 0 1px rgba(0, 0, 0, 0.5);
  border-radius: 4px;
  color: #313435;
  font-weight: 400;
  text-align: center;
  text-transform: capitalize;
}
.tooltip-wrap .tooltip-content {
  padding: 0.5em;
}
.tooltip-wrap .tooltip-pointer {
  position: absolute;
  width: 22.5px;
  height: 10px;
  background: transparent url('images/tooltip/tooltip-bg-pointer@2x.png') no-repeat;
  background-size: 22.5px 10.5px;
  margin-left: -11.2px;
}
.tooltip-wrap.above .tooltip-pointer {
  transform: rotate(0deg);
  bottom: -9px;
}
.tooltip-wrap.below .tooltip-pointer {
  transform: rotate(180deg);
  top: -9px;
}
