css:
.item-count-box {
  float: right;
  min-width: 26px;
  text-align: center;
  display: inline;
  font-size: 12px;
  padding: 1px 5px 1px 5px;
  margin-top: 3.5px;
  line-height: 20.9px;
  border-radius: 3px;
}
.event-wrapper {
  cursor: default;
  position: relative;
  font-size: 13.95px;
  margin-top: 15px;
  box-shadow: inset 0 0 1px 1px rgba(0, 0, 0, 0.09);
  overflow: hidden;
  text-overflow: ellipsis;
  white-space: nowrap;
  width: 100%;
  display: inline-block;
  border-radius: 4px;
}
.event-wrapper .event-header {
  border-bottom: 1px solid #ececec;
  padding: 10px;
}
.event-wrapper .event-header img {
  margin-right: 20px;
}
.event-wrapper .event-header .event-title-text {
  color: rgba(35, 31, 32, 0.5);
}
.event-wrapper .event-header .event-title {
  color: #231f20;
}
.event-wrapper .event-body {
  padding: 10px;
}
.event-wrapper .event-body .event-date {
  display: inline-block;
  width: 100%;
}
.event-wrapper .event-body .event-date .event-day {
  display: block;
  font-size: 15.9px;
  color: #e64d65;
}
.event-wrapper .event-body .event-date .event-time {
  display: inline-block;
  font-size: 25.5px;
  font-weight: 300;
}
.event-wrapper .event-body .event-actions {
  display: inline-block;
  float: right;
  z-index: 4;
  text-align: center;
}
.event-wrapper .event-body .event-actions .btn-rsvp {
  float: left;
  padding: 11.25px 26.25px 11.25px 26.25px;
  line-height: 10px;
  color: #231f20;
  border-radius: 3px;
  background: #ffffff;
  box-shadow: 0 1px 4px 0 rgba(0, 0, 0, 0.21);
  margin: 0 7.5px 0 7.5px;
}
.event-wrapper .event-body .event-actions .btn-rsvp:active {
  background: transparent;
}
.event-wrapper .event-body .event-actions .btn-rsvp.no {
  background: #d9534f;
  color: #ffffff;
}
.event-wrapper .event-body .event-actions .btn-rsvp.yes {
  background: #5cb346;
  color: #ffffff;
}
.event-wrapper .event-body .event-actions .btn-rsvp.maybe {
  background: #77828d;
  color: #ffffff;
}
